import React from 'react';

function TodoItem(props) {
  // console.log(props)
  const todo = props.item
  const completeStyle = {
    fontStyle: "italic",
    color: "#777",
    textDecoration: "line-through"
  }
  return (
    <div className="todo-item">
      <input 
        type="checkbox" 
        checked={todo.completed}
        onChange={(e) => props.handleChange(todo.id)}
      />
      <p style={todo.completed ? completeStyle : null}>
        {todo.text}
      </p>
    </div>
  )
}

export default TodoItem;
