import React, {Component} from 'react';

import todosData from './data';
import TodoList from './components/TodoList';
import FilterableProductTable from './components/Product/FilterableProductTable';
import './App.css';

class App extends Component {
  state = {
    todos: todosData,
    isLoading: true,
    value: ''
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({
        isLoading: false
      });
    }, 1500);
  }

  handleChange = id => {
    this.setState(prevState => {
      const updateTodo = prevState.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed;
        }
        return todo;
      });
      return {
        todos: updateTodo
      }
    })
  }

  handleTodo = event => {
    this.setState({value: event.target.value})
  }

  addTodo = event => {
    event.preventDefault();
    const addedTodo = {
      id: Math.floor((Math.random() * 100) + 1),
      text: this.state.value,
      completed: false
    }
    this.setState({todos: [...this.state.todos, addedTodo]});
    this.setState({value: ''});
  }

  deleteTodo = id => {
    console.log(id)
    this.setState({todos: [...this.state.todos.filter(todo => todo.id !== id)] });
  }

  render() {
    return (
      <div className="App">
        {/* {this.state.isLoading ?
          <h3>TodoApp is loading...</h3> :
          <TodoList
            data={{
              ...this.state,
              handleChange: this.handleChange,
              handleTodo: this.handleTodo,
              addTodo: this.addTodo,
              deleteTodo: this.deleteTodo
            }}
          />
        } */}
        <FilterableProductTable />
      </div>
    );
  }
}

export default App;
