import React, {createContext, useState, useReducer} from 'react';
import {todoReducer} from '../reducer/TodoReducer';
import {todosData} from '../data';

export const TodoContext = createContext();

function TodoContextProvider(props) {
  const initialFormState = {id: null, text: '', completed: false};

  const [todos, dispatch] = useReducer(todoReducer, todosData);
  const [editing, setEditing] = useReducer(todoReducer, false);
  const [currentTodo, setCurrentTodo] = useReducer(todoReducer, initialFormState);

  return (
    <TodoContext.Provider value={{todos, dispatch, editing}}>
      {props.children}
    </TodoContext.Provider>
  )
}

export default TodoContextProvider;
