import React from 'react';

function TodoItem(props) {
  const todo = props.item
  const todoStyle = {
    background: "#f4f4f4",
    padding: "10px",
    borderBottom: "1px dotted #ccc",
    color: todo.completed ? "#777" : "#000",
    fontStyle: todo.completed ? "italic" : "normal",
    textDecoration: todo.completed ? "line-through" : "none"
  }
  const btnStyle = {
    background: 'red',
    color: "#fff",
    border: "none",
    borderRadius: "50%",
    cursor: "pointer",
    float: "right"
  }

  return (
    <div style={todoStyle} className="todo-item">
      <input 
        type="checkbox" 
        checked={todo.completed}
        onChange={(e) => props.handleChange(todo.id)}
      />
      {todo.text}
      <button style={btnStyle}>X</button>
    </div>
  )
}

export default TodoItem;
