import React, {useState} from 'react';

function Example() {
  // They let you use state and other React features 
  // without writing a class.
  const [count, setCount] = useState(0);

  return (
    <div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count +1)}>
        Click me
      </button>
    </div>
  )
}

function ExampleWithManyState() {
  const [age, setAge] = useState(42);
  const [fruit, setFruit] = useState('banana');
  const [todo, setTodos] = useState([{ text: 'Learn Hooks'}]);

  function handleOrangeClick() {
    setFruit('orange');
  }

  return (
    <>
      <button
        onClick={() => handleOrangeClick()}
      >Click to be Orange</button>
      {fruit}
    </>
  )
}

export default ExampleWithManyState;
