import React from 'react';

function TodoItem(props) {
  // console.log(props)
  const todo = props.item
  const completeStyle = {
    fontStyle: "italic",
    color: "#777",
    textDecoration: "line-through"
  }
  const BtnStyle = {
    background: 'red',
    color: "#fff",
    border: "none",
    borderRadius: "50%",
    cursor: "pointer",
    float: "left"
  }

  return (
    <div className="todo-item">
      <input 
        type="checkbox" 
        checked={todo.completed}
        onChange={(e) => props.handleChange(todo.id)}
      />
      <p style={todo.completed ? completeStyle : null}>
        {todo.text}
      </p>
      <button style={BtnStyle}>X</button>
    </div>
  )
}

export default TodoItem;
