class TodoForm extends Component {
  render() {
    return (
    <form onSubmit={this.props.addTodo}>
      <label htmlFor="todo">
        <input type="text" value={this.props.value} onChange={this.props.handleTodo} />  
      </label>
      <button>X</button>
      <input type="submit" value="Submit"/>
    </form>
    )
  }
}

export default TodoForm;
