import todosData from './data';
import TodoList from './components/TodoList';
import './App.css';

class App extends Component {
  state = {
    todos: todosData,
    isLoading: true
  }

  componentDidMount() {
    // setTimeout(() => {
    //   this.setState({
    //     isLoading: false
    //   });
    // }, 1500);
  }

  handleChange = id => {
    this.setState(prevState => {
      console.log(prevState)
    })
  }

  render() {
    return (
      <div className="App">
        <TodoList
          data={{
            ...this.state,
            handleChange: this.handleChange
          }}
        />
      </div>
    );
  }
}

export default App;
