import React, {useContext} from 'react';
import {TodoContext} from '../context/TodoContext';

function TodoItem({todo}) {
  const todoItemStyle = {
    background: '#ccc',
    padding: '5px 5px',
    width: '100%',
    borderBottom: '1px dotted #fff'
  };
  const deleteBtnStyle ={
    padding: '5px 3px',
    background: 'red',
    borderRadius: '50%',
    marginLeft: '80%'
  }
  const {toggleTodo} = useContext(TodoContext);
  return (
    <div className='todo-item' style={todoItemStyle}>
      <p style={{textDecoration: todo.completed ? 'line-through' : ''}}>
        <input type='checkbox' onChange={() => toggleTodo(todo.id)} style={{marginRight: '.75rem' }} />
        {todo.text}
      </p>
      <button style={deleteBtnStyle}>X</button>
    </div>
  )
}

export default TodoItem;
